INSULT_ADJECTIVES = ["–±–µ–∑—á–µ—Å—Ç–Ω—ã–π", "–Ω–µ–æ—Ç–µ—Å–∞–Ω–Ω—ã–π", "–≤–∑–¥–æ—Ä–Ω—ã–π", "—Å–∫–∞—Ä–µ–¥–Ω—ã–π", "—Ä–∞—Å–ø—É—Ç–Ω—ã–π", "–Ω–µ–ø—Ä–æ—Å–≤—£—â–µ–Ω–Ω—ã–π", "–º—£–ª–æ—á–Ω—ã–π"]
INSULT_NOUNS = ["–Ω–µ–≥–æ—Ü—ñ–∞–Ω—Ç—ä", "–ø—Ä–æ–π–¥–æ—Ö–∞", "–≤–∏—Ä—à–µ–ø–ª–µ—Ç—ä", "–≥–∏–º–Ω–∞–∑–∏—Å—Ç–∫–∞", "–º–æ–¥–∏—Å—Ç–∫–∞", "—Ñ–∞—Ä–º–∞–∑–æ–Ω—ä", "–Ω–∏–≥–∏–ª–∏—Å—Ç—ä", "–ø—Ä–æ–∂–µ–∫—Ç—ë—Ä—ä"]

CONSONANTS_FOR_YER = "–±–≤–≥–¥–∂–∑–∫–ª–º–Ω–ø—Ä—Å—Ç—Ñ—Ö—Ü—á—à—â"

def yatify_word(word):
    new_word = ""
    if "–º–∏—Ä" in word: word = word.replace("–º–∏—Ä", "–ºi—Ä—ä")
    for i, char in enumerate(word):
        if char.lower() == '–µ' and random.random() < 0.4:
            chance = 0.6 if i > 0 and i < len(word) - 1 else 0.2
            new_word += '—£' if char.islower() else '—¢' if random.random() < chance else char
        else: new_word += char
    return new_word

def fitafy_word(word):
    if random.random() < 0.38:
        return word.replace('—Ñ', '—≥').replace('–§', '—≤')
    return word

def generate_imperial_insult():
    return f"–í—ã, {random.choice(IMPERIAL_ADDRESSES).split(' ')[-1].lower()}, –ø–æ–ø—Ä–æ—Å—Ç—É {random.choice(INSULT_ADJECTIVES)} {random.choice(INSULT_NOUNS)}!"

def imperial_transform(text: str) -> str:
    if not text: return ""

    for modern, old_variants in WORD_REPLACEMENTS.items():
        text = re.sub(r'\b' + re.escape(modern) + r'\b', random.choice(old_variants), text, flags=re.IGNORECASE)

    words = text.split(' ')
    result_words = []
    for word in words:
        processed_word = yatify_word(fitafy_word(word))
        processed_word = re.sub(r'–∏([–∞–µ—ë–æ—É—ã—ç—é—è])', r'i\1', processed_word, flags=re.IGNORECASE)
        processed_word = re.sub(r'–ò([–∞–µ—ë–æ—É—ã—ç—é—è])', r'I\1', processed_word)
        if processed_word and processed_word[-1].lower() in CONSONANTS_FOR_YER:
            processed_word += '—ä'
        result_words.append(processed_word)
    text = ' '.join(result_words)

    if random.random() < 0.6:
        words = text.split()
        if len(words) > 1:
            if random.random() < 0.5: # –í—Å—Ç–∞–≤–ª—è–µ–º –æ–±—Ä–∞—â–µ–Ω–∏–µ
                injection = f"<b>{random.choice(IMPERIAL_ADDRESSES)},</b>"
                words.insert(random.randint(0, len(words)), injection)
            else: # –í—Å—Ç–∞–≤–ª—è–µ–º —Ñ—Ä–∞–∑–æ—á–∫—É
                injection = random.choice(IMPERIAL_PHRASE_INJECTIONS)
                if injection.endswith(('.', '?', '!')): # –≤ –∫–æ–Ω–µ—Ü
                    words.append(injection)
                elif injection.endswith(','): # –≤ –Ω–∞—á–∞–ª–æ
                    words.insert(0, injection)
                else: # –≤ —Å–µ—Ä–µ–¥–∏–Ω—É
                    words.insert(random.randint(1, len(words)), f"<i>{injection}</i>")
            text = ' '.join(words)
    
    if random.random() < 0.15:
        text += f"\n\n<i>{generate_imperial_insult()}</i>"

    return text
